package com.example.textsummarizer.summarizer;

import java.io.File;
import java.io.IOException;

import static com.example.textsummarizer.summarizer.dataset.DatasetReader.*;

public class DemoDriver {

    public static void main(String[] args) throws IOException, ClassNotFoundException {

        File f = new File("wordVectorData.obj");
        if(f.exists()){
            System.out.println("binary dataset exists. taking input");
            objectInput();
        }
        else {
            System.out.println("binary dataset does not exists. reading from text file");
            File ft = new File("cc.bn.300.vec");
            if(ft.exists()){
                readFromTextFile();
                objectOutput();
            }
            else {
                System.out.println("dataset does not exist. please download the cc.bn.300.vec dataset (text file) from https://fasttext.cc/docs/en/crawl-vectors.html");
            }
        }
        SummarizerStartingPoint.getInstance().driver(" শেখ মুজিবুর রহমান ১৯২০ খ্রিষ্টাব্দের ১৭ই মার্চ (৩রা চৈত্র ১৩২৭ বঙ্গাব্দ) রাত ৮ টায় তৎকালীন ব্রিটিশ ভারতের বেঙ্গল প্রেসিডেন্সির অন্তর্ভুক্ত ফরিদপুর জেলার গোপালগঞ্জ মহকুমার পাটগাতি ইউনিয়নের বাইগার নদী তীরবর্তী টুঙ্গিপাড়া গ্রামে জন্মগ্রহণ করেন। তিনি শেখ বংশের গোড়াপত্তনকারী শেখ বোরহানউদ্দিনের বংশধর। তার বাবা শেখ লুৎফুর রহমান গোপালগঞ্জ দায়রা আদালতের সেরেস্তাদার বা হিসাব সংরক্ষণকারী ছিলেন এবং তার মা সায়েরা খাতুন। চার কন্যা এবং দুই পুত্রের মধ্যে তিনি ছিলেন তৃতীয়। শেখ মুজিবুর রহমানের বড় বোনের নাম ফাতেমা বেগম, মেজ বোন আছিয়া বেগম, সেজ বোন হেলেন ও ছোট বোন লাইলী এবং তার ছোট ভাইয়ের নাম শেখ আবু নাসের। তার নানা শেখ আবদুল মজিদ তার নামকরণ করেন “শেখ মুজিবুর রহমান”। তার ছোটবেলার ডাকনাম ছিল “খোকা”। ছোটবেলা থেকেই তিনি মানুষের প্রতি সহমর্মী স্বভাবের অধিকারী ছিলেন। দুর্ভিক্ষের সময় নিজের গোলা থেকে ধান বিতরণ করতেন। সমিতি করে অন্যদের কাছ থেকে ধান-চাল সংগ্রহ করে গরিব ছাত্রদের মধ্যে বিলি করতেন। ১৯৪০ খ্রিষ্টাব্দে ফুটবল খেলায় ট্রফি বিজেতা শেখ মুজিব। ১৯২৭ খ্রিষ্টাব্দে সাত বছর বয়সে শেখ মুজিব গিমাডাঙ্গা প্রাথমিক বিদ্যালয়ে পড়াশোনা শুরু করেন। নয় বছর বয়সে ১৯২৯ খ্রিষ্টাব্দে গোপালগঞ্জ পাবলিক বিদ্যালয়ে তৃতীয় শ্রেণিতে ভর্তি হন। পিতার বদলিজনিত কারণে ১৯৩১ খ্রিষ্টাব্দে তিনি মাদারীপুর ইসলামিয়া বিদ্যালয়ে চতুর্থ শ্রেণিতে ভর্তি হন এবং সেখানে ১৯৩৪ খ্রিষ্টাব্দ পর্যন্ত পড়াশোনা করেন। ১৯৩৪ খ্রিষ্টাব্দে তিনি বেরিবেরি নামক জটিল রোগে আক্রান্ত হন এবং তার হৃৎপিণ্ড দুর্বল হয়ে পড়ে। ১৯৩৬ খ্রিষ্টাব্দে তার চোখে গ্লুকোমা ধরা পড়ে ও অস্ত্রোপচার করাতে হয় এবং এ থেকে সম্পূর্ণরূপে আরোগ্যলাভ করতে বেশ সময় লেগেছিল। এ কারণে তিনি ১৯৩৪ খ্রিষ্টাব্দ থেকে ১৯৩৮ খ্রিষ্টাব্দ পর্যন্ত চার বছর বিদ্যালয়ের পাঠ চালিয়ে যেতে পারেননি। তিনি ১৯৩৮ খ্রিষ্টাব্দে সুস্থ হবার পর গোপালগঞ্জে মাথুরানাথ ইনস্টিটিউট মিশন স্কুলে সপ্তম শ্রেণিতে ভর্তি হন। এ সময়ে তার গৃহশিক্ষক ছিলেন ব্রিটিশবিরােধী আন্দোলনের সক্রিয় কর্মী এবং বহু বছর জেল খাটা কাজী আবদুল হামিদ (হামিদ মাস্টার) নামীয় জনৈক ব্যক্তি। পরবর্তীকালে গোপালগঞ্জ মিশনারি স্কুল থেকে তিনি ১৯৪২ খ্রিষ্টাব্দে ম্যাট্রিকুলেশন পাশ করেন। ১৯৪৪ খ্রিষ্টাব্দে শেখ মুজিব কলকাতার ইসলামিয়া কলেজ (বর্তমান নাম মৌলানা আজাদ কলেজ) থেকে আই.এ. এবং ১৯৪৭ খ্রিষ্টাব্দে ইতিহাস ও রাষ্ট্রবিজ্ঞান বিষয়ে স্নাতক ডিগ্রি লাভ করেন। কলকাতা বিশ্ববিদ্যালয়ের অধিভুক্ত এই কলেজটি তখন বেশ নামকরা ছিল। ইসলামিয়া কলেজে অধ্যয়নকালীন তিনি বেকার হোস্টেলের ২৪ নং কক্ষে থাকতেন। ১৯৯৮ খ্রিষ্টাব্দে পশ্চিমবঙ্গ সরকার তার সম্মানার্থে ২৩ ও ২৪ নম্বর কক্ষকে একত্র করে “বঙ্গবন্ধু স্মৃতিকক্ষ” তৈরি করে। ২০১১ খ্রিষ্টাব্দের ২৩শে ফেব্রুয়ারি কক্ষটির সম্মুখে তার আবক্ষ ভাস্কর্য স্থাপন করা হয়। ভারত বিভাজনের পর তিনি ঢাকা বিশ্ববিদ্যালয়ে আইন বিষয়ে ভর্তি হন। তবে চতুর্থ শ্রেণির কর্মচারীদের দাবি-দাওয়ার প্রতি বিশ্ববিদ্যালয় কর্তৃপক্ষের উদাসীনতার বিরুদ্ধে বিক্ষোভ প্রদর্শনে উস্কানি দেয়ার অভিযোগে বিশ্ববিদ্যালয় কর্তৃপক্ষ তাকে ১৯৪৯ খ্রিষ্টাব্দে বহিষ্কার করে। পরবর্তীকালে ২০১০ খ্রিষ্টাব্দের ১৪ই আগস্ট ঢাকা বিশ্ববিদ্যালয় কর্তৃপক্ষ এই বহিষ্কারাদেশ প্রত্যাহার করে নেয়। শেখ মুজিবের রাজনৈতিক জীবনের সূচনা ঘটেছিল ১৯৩৯ খ্রিষ্টাব্দে মিশনারি স্কুলে পড়ার সময় থেকে। ঐ বছরই বিদ্যালয় পরিদর্শনে আসেন তদানীন্তন বেঙ্গল প্রেসিডেন্সির মুখ্যমন্ত্রী শেরে বাংলা এ কে ফজলুল হক এবং খাদ্যমন্ত্রী ও পরবর্তীকালে বাংলা প্রদেশ ও পাকিস্তানের প্রধানমন্ত্রীর দায়িত্ব পালনকারী হোসেন শহীদ সোহ্\u200Cরাওয়ার্দী। ঐ সময় বিদ্যালয়ের ছাদ সংস্কারের দাবি নিয়ে একটি দল তাদের কাছে যায়। দলটির নেতৃত্ব দিয়েছিলেন শেখ মুজিব। ব্যক্তিগত রেষারেষির জেরে ১৯৩৮ খ্রিষ্টাব্দে শেখ মুজিবুর রহমানকে প্রথমবারের মতো গ্রেফতার করা হয়। ৭ দিন হাজতবাস করার পর তিনি ছাড়া পান। ১৯৩৯ খ্রিষ্টাব্দে তিনি গোপালগঞ্জ মহকুমা মুসলিম ছাত্রলীগের প্রতিষ্ঠাতা সেক্রেটারি এবং মহকুমা মুসলিম লীগের ডিফেন্স কমিটির সেক্রেটারি নির্বাচিত হন। ১৯৪০ খ্রিষ্টাব্দে নিখিল ভারত মুসলিম ছাত্র ফেডারেশনে যোগ দেন। এ সময়ে তিনি এক বছর মেয়াদের জন্য নিখিল বঙ্গ মুসলিম ছাত্রলীগের কাউন্সিলর নির্বাচিত হন। ১৯৪১ খ্রিষ্টাব্দে ফরিদপুর জেলা ছাত্রলীগের সম্মেলনে কাজী নজরুল ইসলাম, হুমায়ুন কবির, প্রিন্সিপাল ইবরাহীম খাঁ প্রমুখ যোগদান করেন। শেখ মুজিব এই সম্মেলনের অন্যতম আয়োজক ছিলেন। শেখ মুজিবুর রহমান ১৯৪২ খ্রিষ্টাব্দে ইসলামিয়া কলেজে ভর্তি হন। সেখানে অধ্যয়নকালীন তিনি বাংলার অগ্রণী মুসলিম নেতা হোসেন শহীদ সোহ্\u200Cরাওয়ার্দীর সংস্পর্শে আসেন। এম. ভাস্করণ তাকে “সোহ্\u200Cরাওয়ার্দীর ছত্রতলে রাজনীতির উদীয়মান বরপুত্র” হিসেবে আখ্যায়িত করেন। একই বছর কলকাতায় ছাত্রনেতা আবদুল ওয়াসেক প্রমুখের নেতৃত্বে হলওয়েল মনুমেন্ট অপসারণ আন্দোলনে জড়িয়ে পড়েন। ঐ সময় থেকে তিনি সক্রিয়ভাবে ছাত্র রাজনীতি শুরু করেন এবং বিনা প্রতিদ্বন্দ্বিতায় ইসলামিয়া কলেজ ছাত্র ইউনিয়নের সাধারণ সম্পাদক নির্বাচিত হন। ১৯৪৩ খ্রিষ্টাব্দে তিনি বেঙ্গল মুসলিম লীগে যোগ দেন। এখানে তার ছাত্র আন্দোলনের মুখ্য বিষয় ছিল–পৃথক মুসলিম রাষ্ট্র হিসেবে পাকিস্তান প্রতিষ্ঠা। ১৯৪৩ খ্রিষ্টাব্দে বঙ্গীয় মুসলিম লীগের কাউন্সিলর নির্বাচিত হন। ১৯৪৪ খ্রিষ্টাব্দে বর্তমান বাংলাদেশের কুষ্টিয়ায় নিখিল বঙ্গ মুসলিম ছাত্রলীগের সম্মেলন অনুষ্ঠিত হয়। এই সম্মেলনে শেখ মুজিব বিশেষ ভূমিকা পালন করেন। তিনি কলকাতায় বসবাসরত ফরিদপুরবাসীদের নিয়ে গঠিত “ফরিদপুর ডিস্ট্রিক্ট এসোসিয়েশনের” সেক্রেটারি মনোনীত হন। এর দুই বছর পর ইসলামিয়া কলেজ ছাত্র ইউনিয়নের মহাসচিব নির্বাচিত হন।১৯৪০ খ্রিষ্টাব্দে লাহোর প্রস্তাব উত্থাপনের পর মুসলিম লীগ পাকিস্তান প্রতিষ্ঠার জন্য মাঠে নেমে পড়ে। মুসলিম লীগের তরুণ ছাত্রনেতা শেখ মুজিব এ সময় পাকিস্তান প্রতিষ্ঠার আন্দোলনে নিজেকে যুক্ত করেছিলেন। “পাকিস্তান দাবির পক্ষে গণভোট” খ্যাত ১৯৪৬ খ্রিষ্টাব্দের নির্বাচনে শেখ মুজিব বৃহত্তর ফরিদপুর অঞ্চলে লীগের ওয়ার্কার ইনচার্জের দায়িত্বে থেকে একনিষ্ঠভাবে কাজ করেন। তৃণমূল পর্যায়ে সাধারণ কৃষক সমাজের কাছে গিয়ে তিনি পাকিস্তান দাবির ন্যায্যতার বিষয় প্রচার করে ভোট চান। এই নির্বাচনে মুসলিম সংখ্যাগরিষ্ঠ অঞ্চলগুলোতে মুসলিম লীগ বিজয় লাভ করে। তবে একমাত্র বাংলায় তারা একক সংখ্যাগরিষ্ঠতা অর্জন করে এবং সোহ্\u200Cরাওয়ার্দীর নেতৃত্বে সরকার গঠিত হয়। ১৯৪৬ খ্রিষ্টাব্দের ১৬ই আগস্ট প্রত্যক্ষ সংগ্রাম দিবস পালনের সময় কলকাতায় ভয়ানক হিন্দু-মুসলিম দাঙ্গার সূত্রপাত হয়। মুজিব মুসলিমদের রক্ষা এবং দাঙ্গা নিয়ন্ত্রণে আনার জন্য হোসেন শহীদ সোহ্\u200Cরাওয়ার্দীর সাথে বিভিন্ন রাজনৈতিক তৎপরতায় শরিক হন। ঐ সময় সোহ্\u200Cরাওয়ার্দী, আবুল হাশিম, শরৎচন্দ্র বসু প্রমুখের নেতৃত্বে ভারত ও পাকিস্তান কর্তৃত্বের বাইরে অবিভক্ত স্বাধীন বাংলা গঠনের যে “যুক্তবঙ্গ আন্দোলন” সংগঠিত হয়, শেখ মুজিব তাতেও যুক্ত হন। পরবর্তীকালে ভারত ও পাকিস্তান রাষ্ট্র সৃষ্টি নিশ্চিত হলে আসাম প্রদেশের বাঙালি মুসলমান অধ্যুষিত সিলেট জেলার ভাগ্য নির্ধারণে গণভোট অনুষ্ঠিত হয়। শেখ মুজিব সিলেট গণভোটে পাকিস্তানে অন্তর্ভুক্তির পক্ষে সংগঠক ও প্রচারক হিসেবে কাজ করেন। তিনি এসময় প্রায় ৫০০ কর্মী নিয়ে কলকাতা থেকে সিলেট গিয়েছিলেন। গণভোটে জয়লাভ সত্ত্বেও করিমগঞ্জ পাকিস্তান অংশে না থাকা এবং দেশভাগের সীমানা নির্ধারণের সময় পূর্ব পাকিস্তানের বিভিন্ন ভৌগোলিক অপ্রাপ্তির বিষয় নিয়ে স্বীয় আত্মজীবনীতে ক্ষোভ প্রকাশ করেছেন। ব্রিটিশ ভারত থেকে পাকিস্তান ও ভারত পৃথক হবার পর শেখ মুজিব পূর্ব পাকিস্তানে ফিরে ঢাকা বিশ্ববিদ্যালয়ের আইন বিভাগে ভর্তি হন। ১৯৪৭ খ্রিষ্টাব্দের ৭ই সেপ্টেম্বর প্রতিষ্ঠিত পূর্ব পাকিস্তান গণতান্ত্রিক যুবলীগের প্রতিষ্ঠাতা সদস্য হন। ১৯৪৮ খ্রিষ্টাব্দের ৪ঠা জানুয়ারি পূর্ব পাকিস্তান মুসলিম ছাত্রলীগ প্রতিষ্ঠা করেন, যার মাধ্যমে তিনি উক্ত অঞ্চলের অন্যতম প্রধান ছাত্রনেতায় পরিণত হন। এ সময় তিনি সমাজতন্ত্রের দিকে ঝুঁকে পড়েন এবং দারিদ্র্য, বেকারত্ব ও জীবনযাত্রার মান উন্নয়নের জন্য সমাজতন্ত্রকেই একমাত্র সমাধান হিসেবে মনে করতে থাকেন। বাংলাকে অন্যতম রাষ্ট্রভাষা করার দাবি নিয়ে প্রতিষ্ঠিত আন্দোলনে অংশ নেয়ার মাধ্যমে শেখ মুজিবের রাজনৈতিক তৎপরতার সূচনা ঘটে। ১৯৪৮ খ্রিষ্টাব্দের ২৩শে ফেব্রুয়ারি করাচিতে পাকিস্তান গণপরিষদের অধিবেশনে উর্দু বা ইংরেজিতে বক্তব্য দেয়ার প্রস্তাব নাকচ করেন পূর্ব পাকিস্তানের কংগ্রেসের সদস্য ধীরেন্দ্রনাথ দত্ত। তিনি বাংলাকেও গণপরিষদের ভাষা করার দাবি তুলে ধরেন। ঐ সময় পাকিস্তানের প্রধানমন্ত্রী লিয়াকত আলী খান ও পূর্ব পাকিস্তানের মুখ্যমন্ত্রী খাজা নাজিমউদ্দিন বাংলা ভাষার বিরোধিতা করলে প্রস্তাবটি বাতিল হয়ে যায়। এছাড়াও ১৯৪৮ খ্রিষ্টাব্দের ২১শে মার্চ মোহাম্মদ আলী জিন্নাহ উর্দুকে পাকিস্তানের রাষ্ট্রভাষা করার ঘোষণা দেন। এতে পূর্ব পাকিস্তানে বিরূপ প্রতিক্রিয়ার সৃষ্টি হয়। প্রতিবাদী শেখ মুজিব অবিলম্বে মুসলিম লীগের এই পূর্ব পরিকল্পিত সিদ্ধান্তের বিরুদ্ধে আন্দোলন শুরু করার সিদ্ধান্ত নেন। একই বছরের ২রা মার্চে ঢাকা বিশ্ববিদ্যালয়ের ফজলুল হক মুসলিম হলে বিভিন্ন রাজনৈতিক দলের নেতাদের অংশগ্রহণে একটি সম্মেলন অনুষ্ঠিত হয়। ঐ সম্মেলনে মুসলিম লীগের বিরুদ্ধে আন্দোলনের নীতিমালা নিয়ে আলোচনা করা হয়। শেখ মুজিব একটি প্রস্তাব পেশ করেছিলেন যা থেকে সর্বদলীয় সংগ্রাম পরিষদ গঠনের সিদ্ধান্ত নেয়া হয়। ঐ পরিষদের আহ্বানে ১১ই মার্চ ১৯৪৮ খ্রিষ্টাব্দে ঢাকায় ধর্মঘট পালিত হয়। ধর্মঘট পালনকালে শেখ মুজিবসহ আরও কয়েকজন রাজনৈতিক কর্মীকে সচিবালয়ের সামনে থেকে গ্রেফতার করা হয়। কিন্তু ছাত্রসমাজের তীব্র প্রতিবাদের মুখে ১৫ই মার্চ শেখ মুজিব এবং অন্যান্য ছাত্র নেতাকে মুক্তি দেয়া হয়। তাদের মুক্তি উপলক্ষে সর্বদলীয় রাষ্ট্রভাষা সংগ্রাম পরিষদ ঢাকা বিশ্ববিদ্যালয়ের বটতলায় শোভাযাত্রা হয় যাতে শেখ মুজিব সভাপতিত্ব করেন। তবে পুলিশ এই শোভাযাত্রা অবরোধ করে রেখেছিল। ১৫ই মার্চ মুজিবের নেতৃত্বে রাষ্ট্রভাষা সংগ্রাম পরিষদ গড়ে তোলা হয়। পুলিশি কার্যক্রমের বিরুদ্ধে শেখ মুজিব অবিলম্বে ১৭ই মার্চ ১৯৪৮ খ্রিষ্টাব্দে দেশব্যাপী ছাত্র ধর্মঘটের ঘোষণা দেন। ১৯শে মার্চ তিনি ঢাকা বিশ্ববিদ্যালয়ের চতুর্থ শ্রেণির কর্মচারীদের অধিকার আদায়ের লক্ষ্যে একটি আন্দোলন পরিচালনা করেন। একই সময়ে ফরিদপুরে কর্ডন প্রথার বিরুদ্ধে আন্দোলন করায় সেই বছরেরই ১১ই সেপ্টেম্বর তারিখে তাকে আবার আটক করা হয়।\n১৯৪৯ খ্রিষ্টাব্দের ২১শে জানুয়ারি শেখ মুজিবকে জেল থেকে মুক্তি দেয়া হয়। জেল থেকে বেরিয়ে তিনি আবার চতুর্থ শ্রেণির কর্মচারীদের দাবি আদায়ের আন্দোলনে জড়িয়ে পড়েন যার জন্য তাকে বিশ্ববিদ্যালয় থেকে জরিমানা করা হয়। কিন্তু তিনি এই জরিমানাকে অবৈধ ঘোষণা করে তা প্রদান করা থেকে বিরত থাকেন। এরই ধারাবাহিকতায় ২৬শে এপ্রিল ১৯৪৯ খ্রিষ্টাব্দে মুসলিম লীগ বিরোধী প্রার্থী শামসুল হক টাঙ্গাইলের উপ-নির্বাচনে বিজয় লাভ করেন।[৪২] শেখ মুজিব তার সেই আন্দোলনের সফলতার জন্য উপাচার্যের বাসভবনের সামনে অনশন ধর্মঘট করেন যার জন্য তাকে পুনরায় আটক করা হয়। এ সময়েই তাকে ঢাকা বিশ্ববিদ্যালয় থেকে বহিষ্কার করা হয়। তার বিরুদ্ধে অভিযোগ ছিল বিশ্ববিদ্যালয়ের চতুর্থ শ্রেণির কর্মচারীদের অধিকার আদায়ের আন্দোলনে নেতৃত্ব প্রদান করা। উল্লেখ্য যে, মৃত্যু-পরবর্তীকালে ২০১০ খ্রিষ্টাব্দের ১৪ই আগস্ট তার হৃত ছাত্রত্ব ঢাকা বিশ্ববিদ্যালয় কর্তৃপক্ষ ফিরিয়ে দেয়।[২৩][৪৩]\n২৬শে জুন, ১৯৪৯ খ্রিষ্টাব্দে পুরাতন ঢাকা কেন্দ্রীয় কারাগার থেকে মুক্তি লাভের পর শেখ মুজিবুর রহমান\n১৯৫০ খ্রিষ্টাব্দের জানুয়ারি মাসে পাকিস্তানের প্রধানমন্ত্রী লিয়াকত আলি খানের পূর্ব পাকিস্তান আগমনকে উপলক্ষ করে আওয়ামী মুসলিম লীগ ঢাকায় দুর্ভিক্ষবিরোধী মিছিল বের করে। এই মিছিলের নেতৃত্ব দেয়ার কারণে এবারও শেখ মুজিবকে আটক করা হয় এবং দুই বছর জেলে আটক করে রাখা হয়।[৪৪] ১৯৫২ খ্রিষ্টাব্দের ২৬শে জানুয়ারি মুজিবের জেলমুক্তির আদেশ পাঠ করার কথা থাকলেও খাজা নাজিমুদ্দিন ঘোষণা করেন, “উর্দুই পাকিস্তানের একমাত্র রাষ্ট্রভাষা হবে।” এ ঘোষণার পর জেলে থাকা সত্ত্বেও প্রয়োজনীয় দিক-নির্দেশনা দিয়ে রাষ্ট্রভাষা সংগ্রাম পরিষদকে পরোক্ষভাবে পরিচালনার মাধ্যমে প্রতিবাদ ও প্রতিরোধ আয়োজনে তিনি সাহসী ভূমিকা রাখেন। এরপরই ২১শে ফেব্রুয়ারিকে রাষ্ট্রভাষার দাবি আদায়ের দিবস হিসেবে পালন করার সিদ্ধান্ত নেয়া হয়। একই সময়ে শেখ মুজিব জেলে অবস্থান করেই ১৪ই ফেব্রুয়ারি থেকে অনশন পালনের সিদ্ধান্ত নেন। তার এই অনশন ১৩ দিন স্থায়ী ছিল।[৩১][৪৫] ২৬শে ফেব্রুয়ারি তাকে জেল থেকে মুক্তি দেয়া হয়।[১৪][৪৬][৪৭]\n১৯৫২ খ্রিষ্টাব্দে সমাজতান্ত্রিক চীনের তৃতীয় প্রতিষ্ঠাবার্ষিকী উপলক্ষে ২রা অক্টোবর থেকে ১২ই অক্টোবর পর্যন্ত রাজধানী পিকিংয়ে এশীয় ও প্রশান্ত মহাসাগরীয় আঞ্চলিক শান্তি সম্মেলন অনুষ্ঠিত হয়। শেখ মুজিবুর রহমান চীন সরকারের আমন্ত্রণে ৩০ সদস্যবিশিষ্ট পাকিস্তান প্রতিনিধি দলের সদস্য হিসেবে এই সম্মেলনে যোগদানের উদ্দেশ্যে চীন সফর করেন।[৪৮][৪৯]\nআওয়ামী লীগ প্রতিষ্ঠা ও যুক্তফ্রন্ট সরকার\nআরও দেখুন: বাংলাদেশ আওয়ামী লীগ ও পূর্ব পাকিস্তানের আইনপরিষদ নির্বাচন, ১৯৫৪\n১৯৫২ খ্রিষ্টাব্দে আওয়ামী লীগ নেতা-কর্মীদের সাথে শেখ মুজিবুর রহমান (মাঝে)\n\n১৯৪৯ খ্রিষ্টাব্দের ২৩শে জুন হোসেন শহীদ সোহ্\u200Cরাওয়ার্দী এবং মাওলানা ভাসানী পূর্ব পাকিস্তান আওয়ামী মুসলিম লীগ গঠন করলে শেখ মুজিব মুসলিম লীগ ছেড়ে দিয়ে এই নতুন দলে যোগ দেন। তাকে দলের পূর্ব পাকিস্তান অংশের যুগ্ম সাধারণ সম্পাদক নির্বাচিত করা হয়।[৫০] তিনি ২৬শে জুন জেল থেকে ছাড়া পান। মুক্তি পাবার পরপরই চলমান খাদ্য সংকটের বিরুদ্ধে আন্দোলনে যোগ দেন। ১৯৪৯ খ্রিষ্টাব্দের সেপ্টেম্বর মাসে ১৪৪ ধারা ভঙ্গের অভিযোগে তাকে সাময়িকভাবে আটক করে রাখা হলেও অচিরেই ছাড়া পেয়ে যান। একই বছরের অক্টোবর মাসে মাওলানা আবদুল হামিদ খান ভাসানীর সাথে যুক্ত থেকে লিয়াকত আলি খানের কাছে একটি প্রতিনিধিদল প্রেরণের চেষ্টা করায় উভয়কেই আটক করা হয়।[৫১]\nযুক্তফ্রন্ট সরকারের কৃষি ও বন মন্ত্রী শেখ মুজিবুর রহমান\n১৯৫৪ খ্রিষ্টাব্দে নবনির্বাচিত যুক্তফ্রন্ট সরকারের কৃষি ও বন মন্ত্রী শেখ মুজিবুর রহমান (বাম থেকে দ্বিতীয়)\nশেখ মুজিবুর রহমান\n১৯৫৬ খ্রিষ্টাব্দে পূর্ব পাকিস্তানের গভর্নর শেরে বাংলা এ কে ফজলুল হকের কাছ থেকে মন্ত্রী হিসেবে শপথ নিচ্ছেন শেখ মুজিবুর রহমান[৫২]\n\n১৯৫৩ খ্রিষ্টাব্দের ৯ই জুলাই শেখ মুজিবকে পূর্ব পাকিস্তান আওয়ামী লীগের কাউন্সিল অধিবেশন শেষে দলের সাধারণ সম্পাদক নির্বাচিত করা হয়।[৫৩][৫৪] একই বছরের ১৪ই নভেম্বর পূর্ব বাংলার প্রাদেশিক পরিষদ নির্বাচনে অংশগ্রহণের জন্য বিভিন্ন দলের সমন্বয়ে যুক্তফ্রন্ট গঠনের সিদ্ধান্ত গৃহীত হয়। ১৯৫৪ খ্রিষ্টাব্দের ১০ই মার্চ এই নির্বাচন অনুষ্ঠিত হয়। নির্বাচনে যুক্তফ্রন্ট ২৩৭টি আসনের মধ্যে ২২৩টিতে বিপুল ব্যবধানে বিজয় অর্জন করে যার মধ্যে ১৪৩টি আসনই আওয়ামী লীগ লাভ করেছিল।[৫৫] শেখ মুজিব গোপালগঞ্জে আসনে ১০,০০০ ভোটের ব্যবধানে বিজয় লাভ করেন।[৫৬] সেখানে তার প্রতিদ্বন্দ্বী ছিলেন মুসলিম লীগ নেতা ওয়াহিদুজ্জামান। ৩রা এপ্রিল শেরে বাংলা এ কে ফজলুল হকের নেতৃত্বে যুক্তফ্রন্ট পূর্ব বাংলা প্রদেশে সরকার গঠন করে এবং ১৫ই মে শেখ মুজিব উক্ত সরকারে যোগ দিয়ে কৃষি, বন ও সমবায় মন্ত্রণালয়ের দায়িত্ব পান।[৫৭] ২৯শে মে কেন্দ্রীয় সরকার যুক্তফ্রন্ট ভেঙে দেয়। ৩১শে মে করাচি থেকে ঢাকা ফেরার পর বিমান বন্দর থেকেই তাকে আটক করা হয়। ২৩শে ডিসেম্বর মুক্তি লাভ করেন তিনি। ১৯৫৫ খ্রিষ্টাব্দের ৫ই জুন শেখ মুজিব প্রথমবারের মতো গণপরিষদের সদস্য হন।[৫৮] ১৭ই জুন আওয়ামী লীগ পল্টন ময়দানে আয়োজিত এক সম্মেলনে ২১ দফা দাবি পেশ করে, যার মধ্যে পূর্ব পাকিস্তানের স্বায়ত্তশাসন অন্তর্ভুক্ত ছিল। ২৩শে জুন দলের কার্যনির্বাহী পরিষদের সভায় সিদ্ধান্ত হয় যে, পূর্ব পাকিস্তানের স্বায়ত্তশাসন অর্জিত না হলে আইনসভার সকল সদস্য পদত্যাগ করবেন। ২৫শে আগস্ট পাকিস্তানের করাচিতে গণপরিষদের অধিবেশনে শেখ মুজিব বলেন:[১৪]\n\n\nস্যার (গণপরিষদের প্রেসিডেন্ট), আপনি দেখবেন ওরা “পূর্ব বাংলা” নামের পরিবর্তে “পূর্ব পাকিস্তান” নাম রাখতে চায়। আমরা বহুবার দাবি জানিয়েছি যে, পাকিস্তানের পরিবর্তে আপনাদের বাংলা (বঙ্গ) ব্যবহার করতে হবে। “বাংলা” শব্দটার একটি নিজস্ব ইতিহাস, ঐতিহ্য আছে। আপনারা এই নাম আমাদের জনগণের সঙ্গে আলাপ-আলোচনা করে পরিবর্তন করতে পারেন। আপনারা যদি ঐ নাম পরিবর্তন করতে চান তাহলে আমাদের বাংলায় আবার যেতে হবে এবং সেখানকার জনগণের কাছে জিজ্ঞেস করতে হবে তারা নাম পরিবর্তনকে মেনে নেবেন কিনা। এক ইউনিটের প্রশ্নটা গঠনতন্ত্রে অন্তর্ভুক্ত হতে পারে। আপনারা এটাকে এখনই কেন তুলতে চান? বাংলা ভাষাকে, রাষ্ট্রভাষা হিসেবে গ্রহণ করার ব্যাপারে কি হবে? যুক্ত নির্বাচনী এলাকা গঠনের প্রশ্নটাই কি সমাধান? আমাদের স্বায়ত্তশাসন সম্বন্ধে ভাবছেন? পূর্ব বাংলার জনগণ অন্যান্য প্রশ্নের সমাধানের সাথে এক ইউনিটের প্রশ্নটাকে বিবেচনা করতে প্রস্তুত। তাই আমি আমার ঐ অংশের বন্ধুদের কাছে আবেদন জানাবো, তারা যেন আমাদের জনগণের রেফারেন্ডাম অথবা গণভোটের মাধ্যমে দেয়া রায়কে মেনে নেন।[৫৯]\n\n১৯৫৫ খ্রিষ্টাব্দের ২১–২৩শে অক্টোবর অনুষ্ঠিত বাংলাদেশ আওয়ামী মুসলিম লীগের বিশেষ অধিবেশনে সর্বসম্মতিক্রমে দলের নাম থেকে “মুসলিম” শব্দটি বাদ দেয়া হয় ও শেখ মুজিবকে পুনরায় দলের সাধারণ সম্পাদক নির্বাচিত করা হয়।[৬০] ৩রা ফেব্রুয়ারি প্রধানমন্ত্রীর সাথে আওয়ামী লীগের বৈঠকে দল থেকে খসড়া সংবিধানে স্বায়ত্তশাসন অন্তর্ভুক্ত করার দাবি জানানো হয়। ১৪ই জুলাই রাষ্ট্রীয় প্রশাসনে সামরিক উপস্থিতির বিরুদ্ধে একটি প্রস্তাব রাখা হয়, যা তিনিই সরকারের কাছে পেশ করেন। ৪ঠা সেপ্টেম্বর তার নেতৃত্বে একটি দুর্ভিক্ষবিরোধী মিছিল বের হয়। ১৪৪ ধারা ভঙ্গের কারণে এই মিছিলে পুলিশ গুলিবর্ষণ করলে কমপক্ষে চারজন নিহত হয়।[৬১] ১৬ই সেপ্টেম্বর শেখ মুজিব প্রাদেশিক সরকারে যোগ দিয়ে একসাথে শিল্প, বাণিজ্য, শ্রম, দুর্নীতিরোধ এবং গ্রামীণ সহায়তা মন্ত্রণালয়ের দায়িত্ব পালন করেন।[৬২] ১৯৫৭ খ্রিষ্টাব্দের ১৫ই জানুয়ারি পাক-ভারত বাণিজ্য চুক্তি সম্মেলনে যোগদান করার জন্য নয়াদিল্লি যান। দলের সাধারণ সম্পাদক হিসেবে পূর্ণাঙ্গ সময় ব্যয় করার জন্যে তিনি ১৯৫৭ খ্রিষ্টাব্দের ৩০শে মে মন্ত্রিপরিষদ থেকে পদত্যাগ করেন।[৬৩][৬৪] ১৯৫৭ খ্রিষ্টাব্দের ৭ই আগস্ট তিনি সরকারি সফরে চীন এবং সোভিয়েত ইউনিয়ন গমন করেন। এই দুই সমাজতান্ত্রিক দেশের নাগরিক জীবন-যাপনের সুবিধা শেখ মুজিবুর রহমানকে সমাজতন্ত্রের প্রতি উজ্জ্বীবিত করে তোলে। ১৯৫৭-৫৮ অর্থবছরের জন্য তিনি পাকিস্তান চা বোর্ডের চেয়ারম্যান নিযুক্ত হন।[২০]\nসামরিক শাসনবিরোধী আন্দোলন\n\n১৯৫৮ খ্রিষ্টাব্দের ৭ই অক্টোবর পাকিস্তানের রাষ্ট্রপতি মেজর জেনারেল ইস্কান্দার মির্জা এবং সেনাবাহিনী প্রধান আইয়ুব খান দেশে সামরিক আইন জারি করেন। আইয়ুব খানের সমালোচনা করার জন্য ১৯৫৮ খ্রিষ্টাব্দের ১১ই অক্টোবর তাকে আটক করা হয়। পরবর্তীতে ১৯৫৯ খ্রিষ্টাব্দের ৫ই অক্টোবর তারিখে তাকে মুক্তি দেওয়া হলেও, তার উপর নজরদারি করা হয়। ১৯৬০ ও ১৯৬১ খ্রিষ্টাব্দে তিনি কার্যত গৃহবন্দি হিসেবে থাকেন।[৪৬] এ সময় আইয়ুব খান ৬ বছরের জন্য সকল ধরনের রাজনৈতিক কর্মকাণ্ড নিষিদ্ধ ঘোষণা করেন। জেলে থাকা অবস্থায় তার বিরুদ্ধে বেশ কয়েকটি ভিত্তিহীন অভিযোগ আনা হয়। ১২ই সেপ্টেম্বরে তাকে দুই বছরের কারাদণ্ড ও পাঁচ হাজার টাকা জরিমানা এবং অনাদায়ে আরও ছয় মাসের কারাদণ্ড দেওয়া হয়।[৬৫] ১৪ মাস একটানা আটক থাকার পর তাকে মুক্তি দেয়া হলেও জেলের ফটক থেকে পুনরায় তাকে গ্রেফতার করা হয়। উচ্চ আদালতে একটি রিট পিটিশন দায়ের করার মাধ্যমে তিনি ১৯৬১ খ্রিষ্টাব্দে ২২ সেপ্টেম্বর জেল থেকে ছাড়া পান।[৬৫][৬৬][৬৭]\n\nজেল থেকে মুক্তি লাভের পর তিনি গুপ্ত রাজনৈতিক তৎপরতা শুরু করেন। অন্যান্য সাধারণ ছাত্রনেতাকে নিয়ে গোপনে নিউক্লিয়াস ও স্বাধীন বাংলা বিপ্লবী পরিষদ নামে সংগঠন গড়ে তোলেন। সংগঠনের উদ্দেশ্য ছিল পূর্ব বাংলার স্বাধীনতা আদায়ের লক্ষ্যে কাজ করা।[৬৬][৬৮] শেখ মুজিব ১৯৬১ খ্রিষ্টাব্দে বাংলার স্বাধীনতার জন্য ভারতের প্রধানমন্ত্রী জওহরলাল নেহরুর কাছে সাহায্য চাইলে তিনি প্রত্যাখ্যাত হন।[৬৭] ১৯৬০ খ্রিষ্টাব্দের ৩রা জানুয়ারি সামরিক সরকার রাজনৈতিক কর্মকাণ্ডের উপর থেকে নিষেধাজ্ঞা তুলে নেয়ার পর শেখ মুজিবুর রহমান দলকে পুনরায় সংগঠিত করার উদ্যোগ নেন।[২৮] ১৯৬২ খ্রিষ্টাব্দের ৬ই ফেব্রুয়ারি জননিরাপত্তা আইনে তাকে আবার আটক করা হয়। ২রা জুন তারিখে চার বছরব্যাপী বহাল থাকা সামরিক আইন তুলে নেয়ার পর ১৮ই জুন তাকে মুক্তি দেয়া হয়।[৪৪][৬৯] ২৫শে জুন তিনি অন্য রাজনৈতিক নেতাদের সাথে মিলে আইয়ুব খান আরোপিত বিভিন্ন রাজনৈতিক বিষয়ের বিরুদ্ধে সংগ্রামে নেমে পড়েন। ৫ই জুন তিনি পল্টন ময়দানে আয়োজিত এক সম্মেলনে আইয়ুব খানের সমালোচনা করেন। ২৪শে সেপ্টেম্বর তিনি লাহোরে যান এবং সেখানে শহীদ সোহ্\u200Cরাওয়ার্দীর সাথে মিলে জাতীয় গণতান্ত্রিক ফ্রন্ট গড়ে তোলেন। এটি মূলত বিরোধী দলসমূহের একটি সাধারণ কাঠামো হিসেবে কাজ করেছিল।\nশহীদ সোহ্\u200Cরাওয়ার্দীর সাথে প্রিয় শিষ্য শেখ মুজিব\n\nপুরো অক্টোবর মাসজুড়ে শহীদ সোহ্\u200Cরাওয়ার্দীর সাথে মিলে যুক্তফ্রন্টের সমর্থন আদায়ের লক্ষ্যে তিনি বাংলার বিভিন্ন স্থান সফর করেন। ১৯৬৩ খ্রিষ্টাব্দে তিনি শহীদ সোহ্\u200Cরাওয়ার্দীর সাথে আলোচনার উদ্দেশ্যে লন্ডন যান। শহীদ সোহ্\u200Cরাওয়ার্দী সেখানে চিকিৎসাধীন ছিলেন ও একই বছরের ৫ ডিসেম্বর বৈরুতে মৃত্যুবরণ করেন।[৭০] এরপর ১৯৬৪ খ্রিষ্টাব্দের ২৫শে জানুয়ারি মুজিবের বাসায় অনুষ্ঠিত এক বৈঠকে আওয়ামী লীগকে পুনরায় সংহত করার সিদ্ধান্ত নেয়া হয়। ঐ বৈঠকের প্রস্তাবের ভিত্তিতে শেখ মুজিবকে আওয়ামী লীগের মহাসচিব[৭১] ও মাওলানা আবদুর রশীদ তর্কবাগীশকে দলের সভাপতি নির্বাচিত করা হয়। ১৯৬৪ খ্রিষ্টাব্দের ১১ই মার্চ একটি সর্বদলীয় সংগ্রাম পরিষদ গঠিত হয়, যার মাধ্যমে মুজিব সাম্প্রদায়িক দাঙ্গা প্রতিরোধকল্পে বিশেষ ব্যবস্থা গ্রহণ করেন। সেনাশাসক রাষ্ট্রপতি আইয়ুব খানের সামরিক শাসন, রাজনীতির নামে মৌলিক গণতন্ত্র (বেসিক ডেমোক্রেসি) প্রচলন এবং পাকিস্তানের কাঠামোতে এক-ইউনিট পদ্ধতির বিরোধী নেতাদের মধ্যে অগ্রগামী ছিলেন শেখ মুজিব।[৭২] মৌলিক গণতন্ত্র অনুযায়ী সারা দেশ থেকে ৮০ হাজার প্রতিনিধি নির্বাচন করা হতো ও তাদের ভোটে রাষ্ট্রপতি নির্বাচিত হতেন। এ পদ্ধতি অনুযায়ী ক্ষমতা কেন্দ্রীভূত করার পরিকল্পনা করা হয়েছিল এবং প্রদেশগুলোকে একত্রে জুড়ে দেওয়ার উদ্যোগ গ্রহণ করা হয়।[৭২] ঐ সময় সামরিক বাহিনীর গণহত্যা আর বাঙালিদের ন্যায্য দাবী পূরণে সামরিক শাসকদের উদাসীনতা পূর্ব পাকিস্তানের জনগণকে ক্ষুব্ধ করে তোলে।[৭৩] অন্যান্য রাজনৈতিক দলের সাথে কাজ করতে গিয়ে মুজিব আইয়ুববিরোধী সর্বদলীয় প্রার্থী ফাতেমা জিন্নাহকে সমর্থন করেন।[৭৪][৭৫] নির্বাচনের দুই সপ্তাহ পূর্বে ১৯৬৫ খ্রিষ্টাব্দের ৭ই নভেম্বর তারিখে ভারতের দালাল অভিযুক্ত করে তাকে আটক করা হয়। শেখ মুজিবকে রাষ্ট্রদ্রোহিতা এবং আপত্তিকর প্রস্তাব পেশের অভিযোগে অভিযুক্ত করে এক বছরের কারাদণ্ডে দণ্ডিত করা হয়।[৭৬] অবশ্য উচ্চ আদালতের এক রায়ে নির্দিষ্ট সময়ের পূর্বেই তিনি মুক্তি পেয়ে যান।\nছয় দফা আন্দোলন\nমূল নিবন্ধ: ছয় দফা আন্দোলন\nশেখ মুজিবুর রহমান লাহোরে ছয় দফা দাবি উপস্থাপন করছেন\n\nজনসংখ্যায় সংখ্যাগরিষ্ঠ এবং পাকিস্তানের মোট রপ্তানি আয়ের সংখ্যাগরিষ্ঠ অংশ (যেমন পাট) পূর্ব পাকিস্তান থেকে হবার পরও এতদাঞ্চলের জনগণের প্রতি সর্বস্তরে বৈষম্য করা হতো।[৭৭] এছাড়াও পূর্ব ও পশ্চিম পাকিস্তানের রাজনৈতিক ক্ষমতা ও অর্থনৈতিক সুবিধা আনুপাতিক হারে ছিল না। পূর্ব পাকিস্তানের আঞ্চলিকভিত্তিতে ক্রমাগত বৈষম্যের শিকার হওয়ায় বিরূপ প্রভাব পড়তে শুরু করে ও প্রতিকূল পরিস্থিতির সম্মুখীন হয়। এর ফলে, অর্থনীতিবিদ, বুদ্ধিজীবী এবং পূর্ব পাকিস্তানের রাজনীতিবিদরা বৈষম্য সম্পর্কে আপত্তি জানাতে শুরু করেন।[৭৮] বৈষম্য নিরসনে শেখ মুজিব ছয়টি দাবি উত্থাপন করেন, যা ছয় দফা দাবি হিসেবে পরিচিত। বাঙালির বহু আকাঙ্ক্ষিত এই দাবি পরবর্তীকালে বাঙালির “প্রাণের দাবি” ও “বাঁচা মরার দাবি” হিসেবে পরিচিতি পায়।[৭৯][৮০] ১৯৬৬ খ্রিষ্টাব্দের ৫ ফেব্রুয়ারি লাহোরে বিরোধী দলসমূহের একটি জাতীয় সম্মেলন অনুষ্ঠিত হয়।[১৪] এ সম্মেলনেই শেখ মুজিব তার ঐতিহাসিক ছয় দফা দাবি পেশ করেন, যা ছিল কার্যত পূর্ব পাকিস্তানের স্বায়ত্তশাসনের পরিপূর্ণ রূপরেখা। ছয় দফার দাবিগুলো ছিল নিম্নরূপ–\n\n        যুক্তরাষ্ট্রীয় ব্যবস্থাধীনে সংসদীয় পদ্ধতির সরকার হবে। সার্বজনীন ভোটাধিকারের ভিত্তিতে নির্বাচন অনুষ্ঠান।\n        কেন্দ্রীয় সরকারের ক্ষমতা কেবল মাত্র দুইটি ক্ষেত্রেই সীমাবদ্ধ থাকবে–দেশরক্ষা ও বৈদেশিক নীতি। অবশিষ্ট সকল বিষয়ে অঙ্গরাষ্ট্রগুলোর ক্ষমতা থাকবে নিরঙ্কুশ।\n        সমগ্র দেশের জন্যে দুইটি পৃথক অথচ অবাধে বিনিময়যোগ্য মুদ্রা, না হয় বিশেষ শর্তসাপেক্ষে একই ধরনের মুদ্রা প্রচলন।\n        ফেডারেশনের অঙ্গরাষ্ট্রগুলোর কর বা শুল্ক ধার্যের ব্যাপারে সার্বভৌম ক্ষমতা থাকবে। তবে, প্রয়োজনীয় ব্যয় নির্বাহের জন্য অঙ্গরাষ্ট্রীয় রাজস্বের একটি অংশ কেন্দ্রীয় সরকারের প্রাপ্য হবে।\n        অঙ্গরাষ্ট্রগুলো নিজেদের অর্জিত বৈদেশিক মুদ্রার মালিক হবে, এর নির্ধারিত অংশ তারা কেন্দ্রকে দেবে।\n        আঞ্চলিক সংহতি ও শাসনতন্ত্র রক্ষার জন্য শাসনতন্ত্রে অঙ্গরাষ্ট্রগুলোকে স্বীয় কর্তৃত্বাধীনে আধা সামরিক বা আঞ্চলিক সেনাবাহিনী গঠন ও রাখার ক্ষমতা দিতে হবে।[৮১]\n\n১৯৭৩ খ্রিষ্টাব্দে শেখ মুজিব সরকার সেনাবাহিনীর জন্য নগদ অর্থে আধুনিক বেতারযন্ত্র ক্রয় করে এবং সিগন্যাল শাখাকে আরও আধুনিক করে গড়ে তােলেন। শেখ মুজিব পাকিস্তান থেকে প্রত্যাবর্তনকারী ত্রিশ হাজারের অধিক সামরিক কর্মকর্তা ও জওয়ানদেরকে বাংলাদেশ সেনাবাহিনীতে পুনর্বাসিত করেন। প্রত্যাবর্তনকারী বাঙালি কর্মকর্তার সংখ্যা ছিল প্রায় ১৩০০। এই সকল কর্মকর্তা ও জওয়ানদের নিয়ে অর্ধ লক্ষের অধিক সদস্যের দেশের প্রথম সেনাবাহিনী গড়ে উঠেছিল। সামরিক সুবিধা বৃদ্ধি করার জন্য শেখ মুজিবের নির্দেশে পার্বত্য চট্টগ্রামের প্রত্যন্ত অঞ্চলে দীঘিনালা, রুমা, আলীকদমের ন্যায় স্থানে ৩টি গুরুত্বপূর্ণ সামরিক ছাউনি গড়ে তোলা হয়।[১৭] ");

    }


}
